package week1day1homework;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intOperator1=5682;
		int intOperator2=235;
		
		double dblOperator1=3438.6728;
		double dblOperator2= 1385.325;
		
		//adding numbers
        System.out.println("Adding...");
        System.out.println("    intOperator1+intOperator2 = " + (intOperator1+intOperator2));
        System.out.println("    dblOperator1+dblOperator2 = " + (dblOperator1+dblOperator2));

        //subtracting numbers
        System.out.println("Subtracting...");
        System.out.println("    intOperator1-intOperator2 = " + (intOperator1-intOperator2));
        System.out.println("    dblOperator1-dblOperator2 = " + (dblOperator1-dblOperator2));

        //Multiplying 
        System.out.println("Multiplying...");
        System.out.println("    intOperator1*intOperator2 = " + (intOperator1*intOperator2));
        System.out.println("    dblOperator1*dblOperator2 = " + (dblOperator1*dblOperator2));

        //Dividing 
        System.out.println("Dividing...");
        System.out.println("    intOperator1/intOperator2 = " + (intOperator1/intOperator2));
        System.out.println("    dblOperator1/dblOperator2 = " + (dblOperator1/dblOperator2));
		
        //Getting the remainder
        System.out.println("Getting the remainder...");
        System.out.println("    intOperator1%intOperator2 = " + (intOperator1%intOperator2));
        System.out.println("    dblOperator1%dblOperator2 = " + (dblOperator1%dblOperator2));
		
        //Adding int to double
        System.out.println("Adding int to double...");
        System.out.println("    intOperator1+dblOperator1 = " + (intOperator1+dblOperator1));
        //Subtracting int from double
        System.out.println("Subtracting int from double...");
        System.out.println("    intOperator1-dblOperator1 = " + (intOperator1-dblOperator1));
        //Multiplying int to double
        System.out.println("Multiplying int to double...");
        System.out.println("    intOperator1*dblOperator1 = " + (intOperator1*dblOperator1));
        //Dividing double by int
        System.out.println("Dividing double by int...");
        System.out.println("    dblOperator1/intOperator1 = " + (dblOperator1/intOperator1));
        //Dividing int by double
        System.out.println("Dividing int by double...");
        System.out.println("    intOperator1/dblOperator1 = " + (intOperator1/dblOperator1));
	}

}
